// src/pages/Todo/TodoPage.js

import React, { useState, useEffect, useCallback, useMemo } from "react";
import TodoForm from "../../components/TodoForm.js";
import TodoList from "../../components/TodoList.js";
import SearchInput from "../../components/SearchInput.js";

const TodoPage = () => {
  const [todos, setTodos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [noResults, setNoResults] = useState(false);

  // Fetch data
  const fetchTodos = useCallback((searchQuery) => {
    setLoading(true);
    setNoResults(false);

    const url = searchQuery
      ? `/api/todos?search=${encodeURIComponent(searchQuery)}`
      : "/api/todos";

    fetch(url)
      .then((response) => {
        if (!response.ok)
          throw new Error(`HTTP error! status: ${response.status}`);
        return response.json();
      })
      .then((data) => {
        // Set noResults based on fetched data
        setNoResults(data.todos.length === 0 && searchQuery !== "");
        setTodos(data.todos);
        setError(null);
      })
      .catch((err) => {
        setError(err.message);
        setTodos([]);
        setNoResults(true); // Treat fetch error as no results for user
      })
      .finally(() => setLoading(false));
  }, []);

  useEffect(() => {
    const timerId = setTimeout(() => {
      fetchTodos(searchTerm);
    }, 500); // Debounce search term to avoid excessive API calls
    return () => clearTimeout(timerId);
  }, [searchTerm, fetchTodos]);

  // Handler functions wrapped in useCallback for performance
  const handleAddTodo = useCallback(
    (task) => {
      fetch(`/api/todos`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ task }),
      })
        .then((response) => response.json())
        .then((data) => {
          setTodos((prevTodos) => [
            ...prevTodos,
            { id: data.id, task: data.task, completed: false },
          ]);
        })
        .catch((err) => console.error("Error adding todo:", err));
    },
    []
  );

  const handleDeleteTodo = useCallback(
    (id) => {
      fetch(`/api/todos/${id}`, {
        method: "DELETE",
      })
        .then(() => {
          setTodos((prevTodos) => prevTodos.filter((todo) => todo.id !== id));
        })
        .catch((err) => console.error("Error deleting todo:", err));
    },
    []
  );

  const handleToggleCompleted = useCallback(
    (id, completed) => {
      fetch(`/api/todos/${id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ completed: !completed }),
      })
        .then(() => {
          setTodos((prevTodos) =>
            prevTodos.map((todo) =>
              todo.id === id ? { ...todo, completed: !completed } : todo
            )
          );
        })
        .catch((err) => console.error("Error updating todo:", err));
    },
    []
  );

  const handleUpdateTodo = useCallback(
    (id, newTask) => {
      fetch(`/api/todos/${id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ task: newTask }),
      })
        .then(() => {
          setTodos((prevTodos) =>
            prevTodos.map((todo) =>
              todo.id === id ? { ...todo, task: newTask } : todo
            )
          );
        })
        .catch((err) => console.error("Error updating task:", err));
    },
    []
  );

  const MemoizedTodoList = useMemo(() => {
    if (noResults) {
      return (
        <p style={{ textAlign: "center", marginTop: "20px" }}>
          Tidak ada data yang ditemukan.
        </p>
      );
    }
    if (todos.length === 0 && searchTerm === "") {
      return (
        <p style={{ textAlign: "center", marginTop: "20px" }}>
          Anda belum memiliki tugas. Silakan tambahkan tugas baru.
        </p>
      );
    }
    return (
      <TodoList
        todos={todos}
        onToggleCompleted={handleToggleCompleted}
        onDeleteTodo={handleDeleteTodo}
        onUpdateTodo={handleUpdateTodo}
      />
    );
  }, [
    todos,
    noResults,
    searchTerm,
    handleToggleCompleted,
    handleDeleteTodo,
    handleUpdateTodo,
  ]);

  if (loading) {
    return <div style={{ textAlign: "center" }}>Loading...</div>;
  }

  if (error) {
    return (
      <div style={{ textAlign: "center", color: "red" }}>Error: {error}</div>
    );
  }

  return (
    <div
      style={{
        padding: "20px",
        maxWidth: "800px",
        margin: "0 auto",
        fontFamily: "sans-serif",
      }}
    >
      <header style={{ textAlign: "center" }}>
        <h1>Aplikasi Todo List</h1>
        <TodoForm onAddTodo={handleAddTodo} />
        <h2>Daftar Tugas Anda</h2>
        <SearchInput
          searchTerm={searchTerm}
          setSearchTerm={setSearchTerm}
        />
      </header>
      {MemoizedTodoList}
    </div>
  );
};

export default TodoPage;
