import React, { useState, useEffect, useCallback } from "react";
import DataTable from 'react-data-table-component';
import { PageContainer, Header, Button, StyledInput, StyledModal, Overlay, ButtonWrapper, SmallButton } from '../../components/Styles';

const TodoPage = () => {
  const [todos, setTodos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentTodo, setCurrentTodo] = useState(null);
  const [taskText, setTaskText] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  // --- REFACTOR 1: Centralized API call wrapper ---
  // This function handles adding the auth token and base URL to every request.
  const apiFetch = useCallback(async (url, options = {}) => {
    const token = localStorage.getItem('token');
    const headers = {
      'Content-Type': 'application/json',
      ...options.headers,
    };

    if (token) {
      headers['Authorization'] = `Bearer ${token}`;
    }

    const response = await fetch(url, { ...options, headers });

    if (response.status === 401) {
      window.location.href = '/login';
      throw new Error('Unauthorized');
    }
    return response;
  }, []);

  const fetchTodos = useCallback(() => {
    setLoading(true);
    apiFetch("/api/todos")
      .then((response) => {
        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setTodos(data.todos || data); 
        setError(null);
      })
      .catch((err) => {
        // Don't set error for 'Unauthorized' as it's a redirect.
        if (err.message !== 'Unauthorized') {
        setError(err.message);
        }
        setTodos([]);
      })
      .finally(() => setLoading(false));
  }, [apiFetch]);

  useEffect(() => {
    fetchTodos();
  }, [fetchTodos]);

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    if (!taskText.trim()) return;

    const method = currentTodo ? "PUT" : "POST";
    const url = currentTodo ? `/api/todos/${currentTodo.id}` : "/api/todos";

    try {
      const response = await apiFetch(url, {
        method: method,
        body: JSON.stringify({ task: taskText }),
      });
      if (!response.ok) throw new Error("Gagal menyimpan data.");
      
      fetchTodos();
      closeModal();
    } catch (err) {
      // --- REFACTOR 2: Better User-Facing Error Handling ---
      setError(err.message);
    }
  };

  const handleDeleteTodo = async (id) => {
    if (window.confirm("Apakah Anda yakin ingin menghapus tugas ini?")) {
      try {
        const response = await apiFetch(`/api/todos/${id}`, { 
          method: "DELETE",
        });
        if (!response.ok) throw new Error("Gagal menghapus tugas.");

        fetchTodos();
      } catch (err) {
        // --- REFACTOR 2: Better User-Facing Error Handling ---
        setError(err.message);
      }
    }
  };

  const openModalForAdd = () => {
    setCurrentTodo(null);
    setTaskText('');
    setModalIsOpen(true);
  };

  const openModalForEdit = (todo) => {
    setCurrentTodo(todo);
    setTaskText(todo.task);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  const columns = [
    { name: 'Tugas', selector: row => row.task, sortable: true, style: { fontSize: '1rem' } },
    {
      name: 'Aksi',
      cell: row => (
        <ButtonWrapper>
          {/* --- REFACTOR 3: Use `color` prop instead of `className` --- */}
          <SmallButton color="info" onClick={() => openModalForEdit(row)}>Update</SmallButton>
          <SmallButton color="danger" onClick={() => handleDeleteTodo(row.id)}>Hapus</SmallButton>
        </ButtonWrapper>
      ),
      width: '180px',
    },
  ];

  const filteredTodos = todos.filter(todo =>
    (todo.task || '').toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (error) {
    return <PageContainer>Error: {error}</PageContainer>;
  }

  return (
    <PageContainer>
      <Header>
        <h1>Aplikasi Todo List</h1>
        {/* --- REFACTOR 3: Use `color` prop instead of `className` --- */}
        <Button color="primary" onClick={openModalForAdd}>Tambah Tugas Baru</Button>
      </Header>

      <StyledInput
        type="text"
        placeholder="Cari tugas..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={{ marginBottom: '20px' }}
      />

      <DataTable
        columns={columns}
        data={filteredTodos}
        progressPending={loading}
        pagination
        highlightOnHover
        striped
        noDataComponent="Tidak ada tugas yang ditemukan. Silahkan tambahkan satu"
      />
      
      <StyledModal isOpen={modalIsOpen} onRequestClose={closeModal} style={Overlay}>
        <h2>{currentTodo ? 'Update Tugas' : 'Tambah Tugas Baru'}</h2>
        <form onSubmit={handleFormSubmit}>
          <StyledInput
            type="text"
            value={taskText}
            onChange={(e) => setTaskText(e.target.value)}
            placeholder="Tuliskan tugasmu..."
            autoFocus
          />
          {/* --- REFACTOR 3: Use `color` prop for buttons --- */}
          <Button color="success" type="submit">Simpan</Button>
          <Button color="danger" type="button" onClick={closeModal} style={{ marginLeft: '10px' }}>Batal</Button>
        </form>
      </StyledModal>
    </PageContainer>
  );
};

export default TodoPage;
