import React from 'react';
import Modal from 'react-modal';

Modal.setAppElement('#root');

export const PageContainer = ({ children }) => (
  <div className="max-w-4xl mx-auto my-8 p-8 bg-white shadow-lg rounded-xl font-sans">
    {children}
  </div>
);

export const Header = ({ children }) => (
  <div className="flex justify-between items-center mb-8">
    {children}
  </div>
);

export const Overlay = {
  // Style object for modal, if needed, though overlayClassName is preferred
};

export const Button = ({ children, onClick, color, type, style }) => {
  const baseClasses = 'px-4 py-2 font-semibold text-white rounded-md transition-colors duration-200';
  let colorClass = '';
  switch (color) {
    case 'primary':
      colorClass = 'bg-indigo-600 hover:bg-indigo-700';
      break;
    case 'success':
      colorClass = 'bg-emerald-600 hover:bg-emerald-700';
      break;
    case 'danger':
      colorClass = 'bg-rose-600 hover:bg-rose-700';
      break;
    case 'info':
      colorClass = 'bg-sky-600 hover:bg-sky-700';
      break;
    default:
      colorClass = 'bg-gray-500 hover:bg-gray-600';
  }
  return (
    <button onClick={onClick} className={`${baseClasses} ${colorClass}`} type={type} style={style}>
      {children}
    </button>
  );
};

export const StyledInput = ({ ...props }) => (
  <input
    className="w-full px-4 py-3 mb-4 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition-shadow duration-200"
    {...props}
  />
);

export const StyledModal = ({ isOpen, onRequestClose, children, style }) => {
  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      className="bg-white p-8 rounded-lg shadow-2xl w-11/12 max-w-lg outline-none transform transition-all duration-300 scale-95"
      overlayClassName="fixed inset-0 bg-black bg-opacity-70 flex justify-center items-center"
      closeTimeoutMS={200}
      style={style}
    >
      {children}
    </Modal>
  );
};

export const ButtonWrapper = ({ children }) => (
  <div className="flex space-x-2">{children}</div>
);

export const SmallButton = ({ children, onClick, color }) => (
  <button
    onClick={onClick}
    className={`px-3 py-1 text-sm font-medium text-white rounded-md transition-colors duration-200 ${
      color === 'primary' ? 'bg-indigo-600 hover:bg-indigo-700' :
      color === 'success' ? 'bg-emerald-600 hover:bg-emerald-700' :
      color === 'danger' ? 'bg-rose-600 hover:bg-rose-700' :
      color === 'info' ? 'bg-sky-600 hover:bg-sky-700' :
      'bg-gray-500 hover:bg-gray-600'
    }`}
  >
    {children}
  </button>
);

export default { PageContainer, Header, Button, StyledInput, StyledModal, Overlay, ButtonWrapper, SmallButton };
